from aiot_ir_receiver import *
from yolobit import *
button_a.on_pressed = None
button_b.on_pressed = None
button_a.on_pressed_ab = button_b.on_pressed_ab = -1

aiot_ir_rx = IR_RX(Pin(pin16.pin, Pin.IN)); aiot_ir_rx.start();

def on_ir_receive_callback(t_C3_ADn_hi_E1_BB_87u, addr, ext):

  display.show(Image.HEART)
  if aiot_ir_rx.get_code() == IR_REMOTE_0:
    pin3.write_analog(round(translate(0, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_1:
    pin3.write_analog(round(translate(10, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_2:
    pin3.write_analog(round(translate(20, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_3:
    pin3.write_analog(round(translate(30, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_4:
    pin3.write_analog(round(translate(40, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_5:
    pin3.write_analog(round(translate(50, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_6:
    pin3.write_analog(round(translate(60, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_7:
    pin3.write_analog(round(translate(70, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_8:
    pin3.write_analog(round(translate(80, 0, 100, 0, 1023)))
  if aiot_ir_rx.get_code() == IR_REMOTE_9:
    pin3.write_analog(round(translate(90, 0, 100, 0, 1023)))
  aiot_ir_rx.clear_code()

aiot_ir_rx.on_received(on_ir_receive_callback)

